"use strict";const available16Chars="abcdef1234567890";function getNumberIn360Cycles(e){return e>360?getNumberIn360Cycles(Math.ceil(e/360*20+16)):e}const getHexChar=e=>{return parseInt(`0x${e}`,16)};function getNumerIn360RangeFromString(e){return getNumberIn360Cycles([...e.length>16?e.substring(0,16):e].filter(e=>" "!==e||!e).map(getHexChar).reduce((e,r,t)=>[...e,isNaN(r)?getHexChar(available16Chars[t]):r],[]).reduce((e,r)=>e+10*r,0))}function getCharHex(e){if(!e||!e.length||"string"!=typeof e)return"0";try{return""+getNumerIn360RangeFromString(e)}catch(e){console.log({error:e})}}function gGetAvatarColor(e,r=45,t=50,n=1){let a=`hsl${1!==n?"a":""}(`;return a+=getCharHex(e=""+e||" "),a+=`, ${r}%, ${t}%${1!==n?", "+n:""})`}module.exports=gGetAvatarColor;
